package br.com.elvisassis.domain.repository;import br.com.elvisassis.api.model.PagedResult;import br.com.elvisassis.domain.model.Courier;import io.quarkus.hibernate.reactive.panache.PanacheRepositoryBase;import io.smallrye.mutiny.Uni;import jakarta.enterprise.context.ApplicationScoped;import java.util.UUID;@ApplicationScopedpublic class CourierRepository implements PanacheRepositoryBase<Courier, UUID> {    public Uni<Courier> findTop1ByOrderByLastFulfilledDeliveryAtAsc(){        return findTop1ByOrderByLastFulfilledDeliveryAtAsc();    }    public Uni<Courier> findByPendingDeliveries_Id(UUID deliveryId) {        return findByPendingDeliveries_Id(deliveryId);    }    public Uni<PagedResult<Courier>> findAllPaged(int page, int size) {        return PaginationHelper.paginate(findAll(), page, size);    }    public Uni<Courier> saveAndFlush(Courier entity) {        return persistAndFlush(entity)                .onItem().transform(v -> entity);    }}