package br.com.elvisassis.domain.service;import jakarta.enterprise.context.ApplicationScoped;import java.math.BigDecimal;import java.math.RoundingMode;@ApplicationScopedpublic class CalculatePayoutService {    public BigDecimal calculate(Double distanceInKm) {        return new BigDecimal("10")                .multiply(new BigDecimal(distanceInKm))                .setScale(2, RoundingMode.HALF_EVEN);    }}