package br.com.elvisassis.api.controller;import br.com.elvisassis.api.model.DeliveryInput;import br.com.elvisassis.domain.model.Delivery;import br.com.elvisassis.domain.repository.DeliveryRepository;import br.com.elvisassis.domain.service.DeliveryPreparationService;import io.smallrye.mutiny.Uni;import jakarta.enterprise.context.ApplicationScoped;import jakarta.inject.Inject;import jakarta.validation.Valid;import jakarta.ws.rs.Consumes;import jakarta.ws.rs.POST;import jakarta.ws.rs.Path;import jakarta.ws.rs.Produces;import jakarta.ws.rs.core.MediaType;import org.jboss.resteasy.reactive.RestResponse;@Path("/api/v1/deliveries")@Consumes(MediaType.APPLICATION_JSON)@Produces(MediaType.APPLICATION_JSON)@ApplicationScopedpublic class DeliveryController {    @Inject    private DeliveryPreparationService deliveryPreparationService;    @Inject    private DeliveryRepository deliveryRepository;    @POST    public Uni<RestResponse<Delivery>> draft(@Valid DeliveryInput input) {        return  deliveryPreparationService.draft(input)                .map(delivery -> RestResponse.status(RestResponse.Status.CREATED, delivery));    }}