package br.com.elvisassis.api.model;import br.com.elvisassis.domain.model.DeliveryStatus;import java.math.BigDecimal;import java.time.LocalDateTime;import java.time.OffsetDateTime;import java.util.List;import java.util.UUID;public class DeliveryResponseDTO {    private UUID id;    private UUID courierId;    private DeliveryStatus status;    private OffsetDateTime placedAt;    private OffsetDateTime assignedAt;    private OffsetDateTime expectedDeliveryAt;    private OffsetDateTime fulfilledAt;    private BigDecimal distanceFee;    private BigDecimal courierPayout;    private BigDecimal totalCost;    private Integer totalItems;    private ContactPointDTO sender;    private ContactPointDTO recipient;    private List<ItemResponseDTO> items;    public DeliveryResponseDTO() {}    public UUID getId() {        return id;    }    public void setId(UUID id) {        this.id = id;    }    public UUID getCourierId() {        return courierId;    }    public void setCourierId(UUID courierId) {        this.courierId = courierId;    }    public DeliveryStatus getStatus() {        return status;    }    public void setStatus(DeliveryStatus status) {        this.status = status;    }    public OffsetDateTime getPlacedAt() {        return placedAt;    }    public void setPlacedAt(OffsetDateTime placedAt) {        this.placedAt = placedAt;    }    public OffsetDateTime getAssignedAt() {        return assignedAt;    }    public void setAssignedAt(OffsetDateTime assignedAt) {        this.assignedAt = assignedAt;    }    public OffsetDateTime getExpectedDeliveryAt() {        return expectedDeliveryAt;    }    public void setExpectedDeliveryAt(OffsetDateTime expectedDeliveryAt) {        this.expectedDeliveryAt = expectedDeliveryAt;    }    public OffsetDateTime getFulfilledAt() {        return fulfilledAt;    }    public void setFulfilledAt(OffsetDateTime fulfilledAt) {        this.fulfilledAt = fulfilledAt;    }    public BigDecimal getDistanceFee() {        return distanceFee;    }    public void setDistanceFee(BigDecimal distanceFee) {        this.distanceFee = distanceFee;    }    public BigDecimal getCourierPayout() {        return courierPayout;    }    public void setCourierPayout(BigDecimal courierPayout) {        this.courierPayout = courierPayout;    }    public BigDecimal getTotalCost() {        return totalCost;    }    public void setTotalCost(BigDecimal totalCost) {        this.totalCost = totalCost;    }    public Integer getTotalItems() {        return totalItems;    }    public void setTotalItems(Integer totalItems) {        this.totalItems = totalItems;    }    public ContactPointDTO getSender() {        return sender;    }    public void setSender(ContactPointDTO sender) {        this.sender = sender;    }    public ContactPointDTO getRecipient() {        return recipient;    }    public void setRecipient(ContactPointDTO recipient) {        this.recipient = recipient;    }    public List<ItemResponseDTO> getItems() {        return items;    }    public void setItems(List<ItemResponseDTO> items) {        this.items = items;    }}