package br.com.elvisassis.api.model;import java.util.UUID;public class ItemResponseDTO {        private UUID id;        private String name;        private Integer quantity;    public ItemResponseDTO() {}    public UUID getId() {        return id;    }    public void setId(UUID id) {        this.id = id;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public Integer getQuantity() {        return quantity;    }    public void setQuantity(Integer quantity) {        this.quantity = quantity;    }}