package br.com.elvisassis.api.model.mapper;import br.com.elvisassis.api.model.ContactPointDTO;import br.com.elvisassis.api.model.DeliveryResponseDTO;import br.com.elvisassis.api.model.ItemResponseDTO;import br.com.elvisassis.domain.model.ContactPoint;import br.com.elvisassis.domain.model.Delivery;import br.com.elvisassis.domain.model.Item;import jakarta.enterprise.context.ApplicationScoped;@ApplicationScopedpublic class DeliveryMapper {    public static DeliveryResponseDTO toResponseDTO(Delivery delivery) {        if (delivery == null) return null;        DeliveryResponseDTO dto = new DeliveryResponseDTO();        dto.setId(delivery.getId());        dto.setStatus(delivery.getStatus());        dto.setPlacedAt(delivery.getPlacedAt());        dto.setExpectedDeliveryAt(delivery.getExpectedDeliveryAt());        dto.setDistanceFee(delivery.getDistanceFee());        dto.setCourierPayout(delivery.getCourierPayout());        dto.setTotalCost(delivery.getTotalCost());        dto.setTotalItems(delivery.getTotalItems());        dto.setSender(toContactPointDTO(delivery.getSender()));        dto.setRecipient(toContactPointDTO(delivery.getRecipient()));        if (delivery.getItems() != null) {            dto.setItems(delivery.getItems().stream()                    .map(DeliveryMapper::toItemResponseDTO)                    .toList());        }        return dto;    }    public static ContactPointDTO toContactPointDTO(ContactPoint cp) {        if (cp == null) return null;        ContactPointDTO dto = new ContactPointDTO();        dto.setZipCode(cp.getZipCode());        dto.setStreet(cp.getStreet());        dto.setNumber(cp.getNumber());        dto.setComplement(cp.getComplement());        dto.setName(cp.getName());        dto.setPhone(cp.getPhone());        return dto;    }    public static ItemResponseDTO toItemResponseDTO(Item item) {        if (item == null) return null;        ItemResponseDTO dto = new ItemResponseDTO();        dto.setId(item.getId());        dto.setName(item.getName());        dto.setQuantity(item.getQuantity());        return dto;    }}