package br.com.elvisassis.domain.event;import java.time.OffsetDateTime;import java.util.UUID;public class DeliveryPlacedEvent {  private final OffsetDateTime occurredAt;  private final UUID deliveryId;  public DeliveryPlacedEvent(OffsetDateTime occurredAt, UUID deliveryId) {    this.occurredAt = occurredAt;    this.deliveryId = deliveryId;  }  public OffsetDateTime getOccurredAt() {    return occurredAt;  }  public UUID getDeliveryId() {    return deliveryId;  }}