package br.com.elvisassis.domain.events;import jakarta.enterprise.context.ApplicationScoped;import jakarta.enterprise.event.Event;import jakarta.inject.Inject;@ApplicationScopedpublic class DeliveryEvents {    @Inject    Event<DeliveryPlacedEvent> deliveryPlaced;    @Inject    Event<DeliveryPickUpEvent> deliveryPickUp;    @Inject    Event<DeliveryFulfilledEvent> deliveryFulfilled;    public void firePlaced(DeliveryPlacedEvent event) {        deliveryPlaced.fire(event);    }    public void firePickUp(DeliveryPickUpEvent event) {        deliveryPickUp.fire(event);    }    public void fireFulfilled(DeliveryFulfilledEvent event) {        deliveryFulfilled.fire(event);    }}