package br.com.elvisassis.domain.model;import jakarta.persistence.Entity;import jakarta.persistence.Id;import jakarta.persistence.ManyToOne;import java.util.UUID;@Entitypublic class Item {  @Id  private UUID id;  private String name;  private Integer quantity;  @ManyToOne(optional = false)  private Delivery delivery;  private void setId(UUID id) {    this.id = id;  }  private void setName(String name) {    this.name = name;  }  void setQuantity(Integer quantity) {    this.quantity = quantity;  }  private void setDelivery(Delivery delivery) {    this.delivery = delivery;  }  public UUID getId() {    return id;  }  public String getName() {    return name;  }  public Integer getQuantity() {    return quantity;  }  public Delivery getDelivery() {    return delivery;  }  public static Item brandNew(String name, Integer quantity, Delivery delivery) {    Item item = new Item();    item.setId(UUID.randomUUID());    item.setName(name);    item.setQuantity(quantity);    item.setDelivery(delivery);    return item;  }}