package br.com.elvisassis.domain.repository;import br.com.elvisassis.api.model.PagedResult;import br.com.elvisassis.domain.model.Delivery;import io.quarkus.hibernate.reactive.panache.PanacheRepositoryBase;import io.quarkus.panache.common.Page;import io.smallrye.mutiny.Uni;import jakarta.enterprise.context.ApplicationScoped;import java.util.UUID;@ApplicationScopedpublic class DeliveryRepository implements PanacheRepositoryBase<Delivery, UUID> {    public Uni<Delivery> saveAndFlush(Delivery entity) {        return persistAndFlush(entity)                .onItem().transform(v -> entity);    }    public Uni<PagedResult<Delivery>> findAllPaged(int page, int size) {        return PaginationHelper.paginate(findAll(), page, size);    }}