package br.com.elvisassis.http.client;import io.smallrye.mutiny.Uni;import jakarta.ws.rs.Consumes;import jakarta.ws.rs.POST;import jakarta.ws.rs.Path;import jakarta.ws.rs.core.MediaType;import org.eclipse.microprofile.rest.client.inject.RegisterRestClient;@Path("/api/v1/couriers")@RegisterRestClient(configKey = "user-api")public interface CourierAPIClient {    @POST    @Path("/payout-calculate")    @Consumes(MediaType.APPLICATION_JSON)    Uni<CourierPayoutResultModel> payoutCalculate(CourierPayoutCalculateInput input);}